const tmpl = document.createElement("template");
tmpl.innerHTML = `
  <style>
    :host {
      display: block;
    }

    :host div.backdrop {
      z-index: 999;
      position: fixed; 
      top: -1000px; 
      bottom: -1000px; 
      left: -1000px; 
      right: -1000px;
      background-color: rgba(0, 0, 0, 0.7);
      display: flex;
      justify-content: center;
      align-items: center;
      flex-direction: column;
    }

    :host div.message {
      font-family: SF Pro Display, Arial;
      font-style: normal;
      font-weight: normal;
      font-size: 18px;
      line-height: 16px;
      text-align: center;
      color: #FFFFFF;
    }

    :host div.message .logo {
      margin-bottom: 32px;
    }

    :host div.message .cta {
      text-decoration: underline;
      cursor: pointer;
    }
  </style>
  <div class="backdrop">
    <div class="message">
      <div class="logo">
        <img alt="" src="data:image/svg+xml;base64," />
      </div>
      <div>
        <p>We&apos;re having some issues loading the checkout screen.</p>
        <p>
          <a
            class="cta"
          >
            Return to store
          </a>
        </p>
      </div>
    </div>
  </div>
`;

export const FastFailureBackdrop = class extends HTMLElement {
  constructor() {
    super();

    const shadowRoot = this.attachShadow({ mode: "open" });
    shadowRoot.appendChild(tmpl.content.cloneNode(true));

    const focusTrigger = shadowRoot.querySelector(".cta");
    focusTrigger?.addEventListener("click", () => {
      this.dispatchEvent(new Event("removebackdrop"));
    });
  }
};
