export const buttonTemplate = document.createElement("template");
export const xIcon =
  "url('data:image/svg+xml;base64,')";

export const checkIcon =
  "url('data:image/svg+xml;base64,')";

export const lockIcon =
  "url('data:image/svg+xml;base64,')";

export const lockIconDarkMode =
  "url('data:image/svg+xml;base64,')";

export const defaultButtonText = "Fast Checkout";

const tooltip = `
  <div class="tooltip">
    <img src="data:image/svg+xml;base64," alt="warning" />
    <span>Please fill out the missing fields</span>
  </div>
`;

buttonTemplate.innerHTML = `
  <style>
    :host {
      display: block;
      width: 100%;
      height: 50px;
      font-family: -apple-system, BlinkMacSystemFont, "Segoe UI", Roboto, Oxygen, Ubuntu, Cantarell, "Fira Sans", "Droid Sans", "Helvetica Neue", sans-serif;
      overflow: visible;
      clear:both;
    }

    :host .wrapper {
      position: relative;
    }

    :host([hidden]) .wrapper {
      display: none;
    }

    :host([invalid]) .tooltip {
      display: block;
    }

    :host .tooltip {
      display: none;
      position: absolute;
      bottom: calc(100% + 32px);
      left: 50%;
      transform: translateX(-50%);
      background: #FFFFFF;
      box-shadow: 0px 8px 20px rgba(0, 0, 0, 0.08);
      border-radius: 6px;
      padding: 24px;
      font-size: 16px;
      color: #000000;
      font-weight: 700;
      white-space: nowrap;
      z-index: 9999;
    }

    :host .tooltip:before {
      position: absolute;
      top: calc(100% - 16px);
      left: 50%;
      transform: translateX(-50%) rotate(45deg);
      content: '';
      height: 30px;
      width: 30px;
      background: #FFFFFF;
      box-shadow: 8px 8px 7px rgba(0, 0, 0, 0.08);
    }

    :host .tooltip img {
      margin-right: 24px;
      vertical-align: middle;
    }

    :host button {
      width: 100%;
      height: 50px;
      border-radius: 8px;
      font-weight: 700;
      font-size: 18px;
      background-color: black;
      color: white;
      cursor: pointer;
      display: flex;
      -webkit-box-align: center;
      align-items: center;
      -webkit-box-pack: center;
      justify-content: center;
      border: 1px solid #000;
      outline-color: transparent;
    }

    :host([dark]) button {
      background-color: white;
      color: black;
    }

    :host button:focus {
      border-color: transparent;
      outline-color: #000;
    }

    :host([cancelled]) button {
      background: #EB5757;
      border-color: #EB5757;
      outline-color: #EB5757;
    }

    :host([completed]) button {
      background: #24B560;
      border-color: #24B560;
      outline-color: #24B560;
    }

    :host([order-again]) button img {
      display: none;
    }

    :host([disabled]) button, :host([invalid]) button {
      background: rgba(0, 0, 0, 0.25);
      color: #FFFFFF;
      border-color: transparent;
      outline: none;
    }

    :host([disabled]) button {
      cursor: default;
    }

    :host #icon {
      margin-right: 8px;
      width: 15px;
      height: 15px;
      position: relative;
      bottom: 1px;
      background-image: ${lockIcon};
      background-size: contain;
      background-repeat: no-repeat;
    }

    :host([dark]) #icon {
      background-image: ${lockIconDarkMode};
    }

  </style>
  <div class="wrapper">
    ${tooltip}
    <button>
    <div id='icon' alt="Secured Fast Checkout"></div>
      <span>${defaultButtonText}</span>
    </button>
  </div>
`;
